void UtList_Add(UtListHead_t *ListHead, void *Data, uint32 DataSize, uint32 Tag)
{
    UtListNode_t *TagHead;
    UtListNode_t *NewNode;
    FILE *log = fopen("/tmp/logs/2813.txt", "a");

    if (log)
    {
        fprintf(log, "UtList_Add ENTRY: ListHead=%p, Data=%p, DataSize=%u, Tag=%u\n",
                (void *)ListHead, Data, (unsigned)DataSize, (unsigned)Tag);
        fflush(log);
    }

    TagHead = UtList_GetHead(ListHead, Tag);

    if (log)
    {
        fprintf(log, "UtList_GetHead returned TagHead=%p\n", (void *)TagHead);
        fflush(log);
    }

    if (TagHead != NULL)
    {
        if (log)
        {
            fprintf(log, "TagHead non-NULL, calling UtList_NewNode(Data=%p, DataSize=%u)\n",
                    Data, (unsigned)DataSize);
            fflush(log);
        }

        NewNode = UtList_NewNode(Data, DataSize);

        if (log)
        {
            fprintf(log, "UtList_NewNode returned NewNode=%p\n", (void *)NewNode);
            if (NewNode == NULL)
            {
                fprintf(log, "WARNING: UtList_NewNode returned NULL. Subsequent dereference may crash. Tag=%u\n", (unsigned)Tag);
            }
            else
            {
                fprintf(log, "About to set NewNode->Tag = %u\n", (unsigned)Tag);
            }
            fflush(log);
        }

        /* Preserve original logic: may dereference NewNode (matching original behavior) */
        NewNode->Tag = Tag;
        UtList_Insert_Before(TagHead, NewNode);

        if (log)
        {
            fprintf(log, "Inserted NewNode=%p before TagHead=%p\n", (void *)NewNode, (void *)TagHead);
            fflush(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "TagHead is NULL, no action taken.\n");
            fflush(log);
        }
    }

    if (log)
    {
        fclose(log);
    }
}
