void UtList_Add(UtListHead_t *ListHead, void *Data, uint32 DataSize, uint32 Tag)
{
    FILE *log = fopen("/tmp/logs/2813.txt", "a");
    if (log == NULL)
    {
        /* Try to create directory and reopen (best-effort). If system/mkdir is not available, fopen will remain NULL and we continue without logging to file). */
        /* Use system() as a portable fallback to ensure /tmp/logs exists on most test environments. */
        int sysrc = 0;
        /* Suppress unused warning if system isn't available at link: still okay. */
        sysrc = system("mkdir -p /tmp/logs");
        (void)sysrc;
        log = fopen("/tmp/logs/2813.txt", "a");
    }

    if (log)
    {
        fprintf(log, "UtList_Add ENTER: ListHead=%p, Data=%p, DataSize=%u, Tag=%u\n",
                (void *)ListHead, Data, (unsigned)DataSize, (unsigned)Tag);
        if (Data == NULL && DataSize > 0)
        {
            fprintf(log, "UtList_Add WARNING: Data==NULL but DataSize>0 (potential read from NULL)\n");
        }
    }

    UtListNode_t *TagHead;
    UtListNode_t *NewNode;

    TagHead = UtList_GetHead(ListHead, Tag);

    if (log)
    {
        fprintf(log, "UtList_GetHead returned TagHead=%p\n", (void *)TagHead);
    }

    if (TagHead != NULL)
    {
        /* Log just before calling the allocator/creator so inputs are captured. */
        if (log)
        {
            fprintf(log, "Calling UtList_NewNode with Data=%p, DataSize=%u\n", Data, (unsigned)DataSize);
            fflush(log);
        }

        NewNode = UtList_NewNode(Data, DataSize);

        if (log)
        {
            fprintf(log, "UtList_NewNode returned NewNode=%p\n", (void *)NewNode);
        }

        /* Preserve original intent: set tag and insert. Avoid additional side-effects.
           However, guard the logging from dereferencing a NULL NewNode. */
        if (NewNode != NULL)
        {
            NewNode->Tag = Tag;
            if (log)
            {
                fprintf(log, "Set NewNode->Tag=%u\n", (unsigned)Tag);
            }
            UtList_Insert_Before(TagHead, NewNode);
            if (log)
            {
                fprintf(log, "UtList_Insert_Before completed: inserted %p before %p\n", (void *)NewNode, (void *)TagHead);
            }
        }
        else
        {
            /* If NewNode is NULL, the original code would have dereferenced it and likely crashed.
               We log this state so callers can see the reason for a failure. We do not allocate or change behavior otherwise. */
            if (log)
            {
                fprintf(log, "NewNode is NULL: skipping tag set and insertion (original code would have dereferenced)\n");
            }
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "TagHead is NULL: no insertion performed\n");
        }
    }

    if (log)
    {
        fprintf(log, "UtList_Add EXIT\n\n");
        fclose(log);
    }
}
