void UtList_Add(UtListHead_t *ListHead, void *Data, uint32 DataSize, uint32 Tag)
{
    UtListNode_t *TagHead;
    UtListNode_t *NewNode;
    FILE *fp = fopen("/tmp/logs/2815.txt", "a");

    /* Fallback if the expected directory does not exist */
    if (fp == NULL)
    {
        fp = fopen("/tmp/2815.txt", "a");
    }

    if (fp != NULL)
    {
        fprintf(fp, "UtList_Add ENTRY: ListHead=%p Data=%p DataSize=%lu Tag=%lu\n",
                (void *)ListHead, (void *)Data, (unsigned long)DataSize, (unsigned long)Tag);
        fflush(fp);
    }

    TagHead = UtList_GetHead(ListHead, Tag);

    if (fp != NULL)
    {
        fprintf(fp, "UtList_Add: UtList_GetHead returned TagHead=%p\n", (void *)TagHead);
        fflush(fp);
    }

    if (TagHead != NULL)
    {
        if (fp != NULL)
        {
            fprintf(fp, "UtList_Add: TagHead non-NULL, calling UtList_NewNode(Data=%p, DataSize=%lu)\n",
                    (void *)Data, (unsigned long)DataSize);
            fflush(fp);
        }

        NewNode = UtList_NewNode(Data, DataSize);

        if (fp != NULL)
        {
            fprintf(fp, "UtList_Add: UtList_NewNode returned NewNode=%p\n", (void *)NewNode);
            fflush(fp);
        }

        /* Preserve original behavior (note: if NewNode is NULL this will reproduce original crash behavior) */
        NewNode->Tag = Tag;

        if (fp != NULL)
        {
            fprintf(fp, "UtList_Add: Set NewNode->Tag=%lu; calling UtList_Insert_Before(TagHead=%p, NewNode=%p)\n",
                    (unsigned long)Tag, (void *)TagHead, (void *)NewNode);
            fflush(fp);
        }

        UtList_Insert_Before(TagHead, NewNode);

        if (fp != NULL)
        {
            fprintf(fp, "UtList_Add: Inserted node (TagHead=%p, NewNode=%p)\n", (void *)TagHead, (void *)NewNode);
            fclose(fp);
        }
    }
    else
    {
        if (fp != NULL)
        {
            fprintf(fp, "UtList_Add: TagHead is NULL, no insertion performed\n");
            fclose(fp);
        }
    }
}
