UtListNode_t *UtList_NewNode(void *Data, uint32 DataSize)
{
    union NodeAllocator
    {
        UtListNode_t Node;
        double       AlignDbl;
        void *       AlignPtr;
        long         AlignLong;
    } * AllocNode;

    /* Probe: log entry, sizes and overflow potential before allocation */
    {
        FILE *log = fopen("/tmp/logs/223.txt", "a");
        if (log != NULL)
        {
            size_t header_size = sizeof(union NodeAllocator);
            size_t requested = header_size + (size_t)DataSize;
            int overflow = (requested < header_size) ? 1 : 0;
            fprintf(log, "UtList_NewNode: ENTER Data=%p DataSize=%u header_size=%zu requested=%zu overflow=%d\n",
                    Data, (unsigned)DataSize, header_size, requested, overflow);
            fclose(log);
        }
    }

    AllocNode = malloc(sizeof(union NodeAllocator) + DataSize);

    /* Probe: log result of malloc and state right before memset */
    {
        FILE *log = fopen("/tmp/logs/223.txt", "a");
        if (log != NULL)
        {
            size_t header_size = sizeof(union NodeAllocator);
            fprintf(log, "UtList_NewNode: malloc returned %p; about to memset(ptr=%p, size=%zu)\n",
                    (void *)AllocNode, (void *)AllocNode, header_size);
            if (AllocNode == NULL)
            {
                fprintf(log, "UtList_NewNode: WARNING - malloc returned NULL; subsequent memset will be invoked on NULL (demonstrates violation scenario)\n");
            }
            fclose(log);
        }
    }

    /* Preserve original behavior (potentially unsafe) to allow reproduction of the violation */
    memset(AllocNode, 0, sizeof(union NodeAllocator));
    AllocNode->Node.Data     = &AllocNode[1];
    AllocNode->Node.DataSize = DataSize;
    memcpy(AllocNode->Node.Data, Data, DataSize);

    AllocNode->Node.Next = &AllocNode->Node;
    AllocNode->Node.Prev = &AllocNode->Node;

    /* Probe: successful completion (this will not be reached if a prior operation crashed) */
    {
        FILE *log = fopen("/tmp/logs/223.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UtList_NewNode: RETURN node=%p data_ptr=%p data_size=%u\n",
                    (void *)&AllocNode->Node, AllocNode->Node.Data, (unsigned)AllocNode->Node.DataSize);
            fclose(log);
        }
    }

    return &AllocNode->Node;
}
