UtListNode_t *UtList_NewNode(void *Data, uint32 DataSize)
{
    union NodeAllocator
    {
        UtListNode_t Node;
        double       AlignDbl;
        void *       AlignPtr;
        long         AlignLong;
    } * AllocNode;

    FILE *log = fopen("/tmp/logs/223.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: UtList_NewNode(Data=%p, DataSize=%lu)\n", Data, (unsigned long)DataSize);
        fflush(log);
    }

    AllocNode = malloc(sizeof(union NodeAllocator) + DataSize);

    if (log)
    {
        fprintf(log, "malloc returned: %p (requested %lu bytes: %lu + %lu)\n",
                (void *)AllocNode,
                (unsigned long)(sizeof(union NodeAllocator) + DataSize),
                (unsigned long)sizeof(union NodeAllocator),
                (unsigned long)DataSize);
        fflush(log);
    }

    /* Preserve original behavior: memset is called immediately after malloc. */
    memset(AllocNode, 0, sizeof(union NodeAllocator));

    if (log)
    {
        fprintf(log, "memset completed on AllocNode=%p (zeroed %lu bytes)\n",
                (void *)AllocNode,
                (unsigned long)sizeof(union NodeAllocator));
        fflush(log);
    }

    /* Compute destination pointer only after memset to avoid evaluating pointer arithmetic on a NULL pointer before the point where original code would crash. */
    AllocNode->Node.Data     = &AllocNode[1];
    AllocNode->Node.DataSize = DataSize;

    if (log)
    {
        fprintf(log, "About to memcpy: dest=%p, src=%p, size=%lu\n",
                (void *)AllocNode->Node.Data,
                Data,
                (unsigned long)DataSize);
        fflush(log);
    }

    memcpy(AllocNode->Node.Data, Data, DataSize);

    if (log)
    {
        fprintf(log, "memcpy completed: dest=%p (first byte=%02x if readable), size=%lu\n",
                (void *)AllocNode->Node.Data,
                /* Do not dereference Data or dest; attempt to read first byte may be invalid. Use a safe placeholder value. */
                0,
                (unsigned long)DataSize);
        fflush(log);
    }

    AllocNode->Node.Next = &AllocNode->Node;
    AllocNode->Node.Prev = &AllocNode->Node;

    if (log)
    {
        fprintf(log, "Node initialized: NodeAddr=%p, Next=%p, Prev=%p, Data=%p, DataSize=%lu\n",
                (void *)&AllocNode->Node,
                (void *)AllocNode->Node.Next,
                (void *)AllocNode->Node.Prev,
                (void *)AllocNode->Node.Data,
                (unsigned long)AllocNode->Node.DataSize);
        fflush(log);
        fclose(log);
    }

    return &AllocNode->Node;
}
