char *UtSprintf(const char *Spec, ...)
{
    va_list                     Args;
    static UT_Snprintf_Buffer_t Buf[UT_SNPRINTF_MAX_BUFS];
    static uint32               TextIndex = 0;
    FILE *log = fopen("/tmp/logs/3095.txt", "a");

    if (log)
    {
        fprintf(log, "UtSprintf ENTERED\n");
        fprintf(log, "  Spec ptr = %p\n", (void *)Spec);
        if (Spec)
        {
            /* log up to 256 chars of the format string to avoid huge logs */
            fprintf(log, "  Spec (preview) = \"%.256s\"\n", Spec);
        }
        fprintf(log, "  TextIndex before = %u\n", (unsigned)TextIndex);
    }

    ++TextIndex;
    if (TextIndex >= UT_SNPRINTF_MAX_BUFS)
    {
        if (log)
            fprintf(log, "  TextIndex >= %u, wrapping to 0\n", (unsigned)UT_SNPRINTF_MAX_BUFS);
        TextIndex = 0;
    }
    else
    {
        if (log)
            fprintf(log, "  TextIndex after increment = %u\n", (unsigned)TextIndex);
    }

    va_start(Args, Spec);
    if (log)
    {
        /* log buffer address and available size information */
        fprintf(log, "  Buffer addr = %p\n", (void *)Buf[TextIndex].Text);
        /* sizeof on the Text member is used to match original behavior */
        fprintf(log, "  Buffer size = %zu\n", sizeof(Buf[TextIndex].Text));
        fprintf(log, "  Calling vsnprintf...\n");
    }

    int result = vsnprintf(Buf[TextIndex].Text, sizeof(Buf[TextIndex].Text), Spec, Args);

    if (log)
    {
        fprintf(log, "  vsnprintf returned = %d\n", result);
        /* log a preview of the produced text to help reproduce read-only use-case */
        fprintf(log, "  Produced text (preview) = \"%.256s\"\n", Buf[TextIndex].Text);
    }

    va_end(Args);

    if (log)
    {
        fprintf(log, "UtSprintf RETURNING addr = %p (non-const char *)\n", (void *)Buf[TextIndex].Text);
        fclose(log);
    }

    return Buf[TextIndex].Text;
}
