void UtTest_Run(void)
{
    /* Local variables (preserve original types to keep behavior identical) */
    UtListNode_t *         UtListMain;
    UtListNode_t *         UtListNode;
    UtTestDataBaseEntry_t *UtTestDataBaseEntry;

    /* Inline include to ensure FILE and fopen are declared in a self-contained replacement. */
    #include <stdio.h>

    FILE *log = fopen("/tmp/logs/1595.txt", "a");
    if (log)
    {
        fprintf(log, "UtTest_Run: entered\n");
        fprintf(log, "UtTest_Run: UtAssert_Global.DataBasePtr=%p\n", (void *)UtAssert_Global.DataBasePtr);
        fflush(log);
    }

    UT_BSP_Lock();

    /*
     * The overall test sequence goes SETUP->TEST->TEARDOWN
     *
     * Combine all registered test groups into a merged group for execution.
     *
     * This could also (theoretically) randomize the order of the "TEST" group
     * while assembling this list, if there was a portable source of entropy.
     */
    UtListMain = UtList_GetHead(UtAssert_Global.DataBasePtr, UTASSERT_GROUP_DEFAULT);
    if (log)
    {
        fprintf(log, "UtTest_Run: UtListMain after DEFAULT head=%p\n", (void *)UtListMain);
        fflush(log);
    }

    UtList_Merge(UtListMain, UtList_GetHead(UtAssert_Global.DataBasePtr, UTASSERT_GROUP_SETUP));
    UtList_Merge(UtListMain, UtList_GetHead(UtAssert_Global.DataBasePtr, UTASSERT_GROUP_TEST));
    UtList_Merge(UtListMain, UtList_GetHead(UtAssert_Global.DataBasePtr, UTASSERT_GROUP_TEARDOWN));

    if (log)
    {
        fprintf(log, "UtTest_Run: merged lists into UtListMain=%p\n", (void *)UtListMain);
        fflush(log);
    }

    UT_BSP_Unlock();

    /*
     * Run through the merged list in order
     */
    for (UtListNode = UtList_GetNext(UtListMain); !UtList_IsEnd(UtListMain, UtListNode);
         UtListNode = UtList_GetNext(UtListNode))
    {
        UtTestDataBaseEntry = UtList_GetObject(UtListNode);

        if (UtTestDataBaseEntry != NULL)
        {
            /* Log the found entry and its relevant state to demonstrate read-only access */
            if (log)
            {
                fprintf(log, "UtTest_Run: processing UtTestDataBaseEntry=%p\n", (void *)UtTestDataBaseEntry);
                fprintf(log, "UtTest_Run:   TestName=%s\n",
                        UtTestDataBaseEntry->TestName ? UtTestDataBaseEntry->TestName : "(null)");
                fprintf(log, "UtTest_Run:   Setup=%p, Test=%p, Teardown=%p\n",
                        (void *)UtTestDataBaseEntry->Setup,
                        (void *)UtTestDataBaseEntry->Test,
                        (void *)UtTestDataBaseEntry->Teardown);
                fflush(log);
            }

            UtAssert_BeginTest(UtTestDataBaseEntry->TestName);

            UtAssert_SetContext(UTASSERT_CASETYPE_TSF);
            if (UtTestDataBaseEntry->Setup)
            {
                if (log)
                {
                    fprintf(log, "UtTest_Run: calling Setup() for %s\n",
                            UtTestDataBaseEntry->TestName ? UtTestDataBaseEntry->TestName : "(null)");
                    fflush(log);
                }
                UtTestDataBaseEntry->Setup();
            }
            UtAssert_SetContext(UTASSERT_CASETYPE_FAILURE);
            if (UtTestDataBaseEntry->Test)
            {
                if (log)
                {
                    fprintf(log, "UtTest_Run: calling Test() for %s\n",
                            UtTestDataBaseEntry->TestName ? UtTestDataBaseEntry->TestName : "(null)");
                    fflush(log);
                }
                UtTestDataBaseEntry->Test();
                UtAssert_Global.ExecutedCount++;
                if (log)
                {
                    fprintf(log, "UtTest_Run: ExecutedCount incremented to %u\n", UtAssert_Global.ExecutedCount);
                    fflush(log);
                }
            }
            UtAssert_SetContext(UTASSERT_CASETYPE_TTF);
            if (UtTestDataBaseEntry->Teardown)
            {
                if (log)
                {
                    fprintf(log, "UtTest_Run: calling Teardown() for %s\n",
                            UtTestDataBaseEntry->TestName ? UtTestDataBaseEntry->TestName : "(null)");
                    fflush(log);
                }
                UtTestDataBaseEntry->Teardown();
            }

            /* Note: We purposely do not modify UtTestDataBaseEntry itself in this function; the
             * pointer is non-const here but is only read. The logs above show all observed
             * accesses and will provide evidence that the structure was not modified. */

            UtAssert_EndTest();

            if (log)
            {
                fprintf(log, "UtTest_Run: finished processing entry %p\n", (void *)UtTestDataBaseEntry);
                fflush(log);
            }
        }
        else
        {
            if (log)
            {
                fprintf(log, "UtTest_Run: UtTestDataBaseEntry is NULL for node %p\n", (void *)UtListNode);
                fflush(log);
            }
        }
    }

    UT_BSP_Lock();

    if (log)
    {
        fprintf(log, "UtTest_Run: destroying database ptr=%p\n", (void *)UtAssert_Global.DataBasePtr);
        fflush(log);
    }

    UtList_Destroy(UtAssert_Global.DataBasePtr);
    UT_BSP_Unlock();

    if (log)
    {
        fprintf(log, "UtTest_Run: calling UT_BSP_EndTest with counters=%p\n", (void *)&UtAssert_GetCounters());
        fclose(log);
    }

    UT_BSP_EndTest(UtAssert_GetCounters());
}
